import React from 'react';
import PlaylistView from '../views/playlistView';

function PlaylistPresenter(props) {
    const [playlist, setPlaylist] = React.useState(props.model.currentPlaylist);
    const [currentSong, setCurrentSong] = React.useState(null);

    React.useEffect(() => {
        const obs = () => {
            setPlaylist(props.model.currentPlaylist)
            setCurrentSong(playlist.currentSong)
        };
        props.model.addObserver(obs);
        return () => {return props.model.removeObserver(obs)};
    }, []);

    return (
        <div>
            {playlist === null || <PlaylistView playlist={playlist}
                playOrPause={(song) => { playlist.setCurrentSong(song) }}
                currentSong={currentSong}
                setPlaylistName={(name) => playlist.setPlaylistName(name)}
                resetName={() => playlist.setPlaylistName("")}/>}
        </div>
    );
}

export default PlaylistPresenter;
